import { SidebarInset, SidebarProvider } from '@/components/ui/sidebar'
import { PropsWithChildren } from 'react'
import { LayoutHeader } from './LayoutHeader'
import { LayoutSidebar } from './LayoutSidebar'
import { LayoutSidebarFooter, LayoutSidebarFooterProps } from './LayoutSidebarFooter'
import { LayoutSidebarHeader, LayoutSidebarHeaderProps } from './LayoutSidebarHeader'
import { LayoutSidebarContent } from './LayoutSidebarContent'
import { LayoutContent } from './LayoutContent'

interface LayoutBaseProps {}

export function LayoutBase({ children }: PropsWithChildren<LayoutBaseProps>) {
  return (
    <>
      <div className="[--header-height:calc(--spacing(14))]">
        <SidebarProvider>
          <div className="flex flex-1">{children}</div>
        </SidebarProvider>
      </div>
    </>
  )
}

interface LayoutProps extends LayoutBaseProps {
  sidebar?: { footer?: LayoutSidebarFooterProps; header?: LayoutSidebarHeaderProps }
}

export function Layout({ sidebar, children }: PropsWithChildren<LayoutProps>) {
  return (
    <LayoutBase>
      <LayoutSidebar>
        <LayoutSidebarHeader {...sidebar?.header} />
        <LayoutSidebarContent />
        <LayoutSidebarFooter {...sidebar?.footer} />
      </LayoutSidebar>
      <SidebarInset>
        <LayoutHeader />
        <LayoutContent>{children}</LayoutContent>
      </SidebarInset>
    </LayoutBase>
  )
}

import { PropsWithChildren } from 'react'

export const LayoutContent = ({ children }: PropsWithChildren) => {
  return <div className="flex flex-1 flex-col gap-4 p-4">{children}</div>
}

import { Sidebar } from '@/components/ui/sidebar'
import { ComponentProps } from 'react'

export const LayoutSidebar = (props: ComponentProps<typeof Sidebar>) => {
  return <Sidebar {...props} />
}

import { Link, useMatches } from '@tanstack/react-router'
import { ChevronRight } from 'lucide-react'

import { Collapsible, CollapsibleContent, CollapsibleTrigger } from '@/components/ui/collapsible'
import {
  SidebarContent,
  SidebarGroup,
  SidebarGroupContent,
  SidebarMenu,
  SidebarMenuButton,
  SidebarMenuItem,
  SidebarMenuSub,
  SidebarMenuSubButton,
  SidebarMenuSubItem,
} from '@/components/ui/sidebar'
import { SidebarItem, useDynamicSidebar } from '@/hooks/useDynamicSidebar'
import { PropsWithChildren, useMemo } from 'react'
import { TooltipContent } from '@/components/ui/tooltip'

export const LayoutSidebarContent = ({ children }: PropsWithChildren) => {
  const sidebarItems = useDynamicSidebar()

  return (
    <SidebarContent>
      <SidebarGroup>
        <SidebarGroupContent>
          <SidebarMenu>
            {sidebarItems.map((item) => (
              <SidebarItemComponent key={item.key} item={item} />
            ))}
          </SidebarMenu>
        </SidebarGroupContent>
      </SidebarGroup>
      {children && (
        <SidebarGroup className="mt-auto">
          <SidebarGroupContent>
            <SidebarMenu>{children}</SidebarMenu>
          </SidebarGroupContent>
        </SidebarGroup>
      )}
    </SidebarContent>
  )
}

const CollapsibleSidebarItem = ({ item }: { item: SidebarItem }) => {
  const matches = useMatches()

  const defaultOpen = useMemo(() => {
    return matches.some((match) => match.pathname === item.url)
  }, [matches, item.url])

  return (
    <Collapsible asChild className="group/collapsible" defaultOpen={defaultOpen}>
      <SidebarMenuItem>
        <CollapsibleTrigger asChild>
          <SidebarMenuButton
            tooltip={item.title ? <TooltipContent>{item.title}</TooltipContent> : undefined}
          >
            {item.icon}
            <span className="text-sm">{item.title}</span>
            <ChevronRight className="ml-auto transition-transform duration-200 group-data-[state=open]/collapsible:rotate-90" />
          </SidebarMenuButton>
        </CollapsibleTrigger>
        <CollapsibleContent>
          <SidebarMenuSub>
            {item.children.map((child) => (
              <Link to={child.url} key={child.url}>
                {({ isActive }) => (
                  <SidebarMenuSubItem>
                    <SidebarMenuSubButton asChild isActive={isActive}>
                      <span className="font-inherit">{child.title}</span>
                    </SidebarMenuSubButton>
                  </SidebarMenuSubItem>
                )}
              </Link>
            ))}
          </SidebarMenuSub>
        </CollapsibleContent>
      </SidebarMenuItem>
    </Collapsible>
  )
}

const SidebarItemComponent = ({ item }: { item: SidebarItem }) => {
  if (item.children.length > 0) {
    return <CollapsibleSidebarItem item={item} />
  }

  return (
    <Link to={item.url} key={item.url}>
      {({ isActive }) => (
        <SidebarMenuItem key={item.key}>
          <SidebarMenuButton asChild isActive={isActive}>
            <span>
              {item.icon}
              <span>{item.title}</span>
            </span>
          </SidebarMenuButton>
        </SidebarMenuItem>
      )}
    </Link>
  )
}

'use client'

import { SidebarFooter, SidebarMenu, SidebarMenuItem } from '@/components/ui/sidebar'
import { PropsWithChildren, use } from 'react'
import { LayoutUserDropdown, LayoutUserDropdownProps } from './LayoutUserDropdown'

export interface LayoutSidebarFooterProps extends LayoutUserDropdownProps {}

export const LayoutSidebarFooter = ({
  children,
  ...userDropdown
}: PropsWithChildren<LayoutSidebarFooterProps>) => {
  return (
    <SidebarFooter>
      <SidebarMenu>
        <SidebarMenuItem>
          <LayoutUserDropdown {...userDropdown}>{children}</LayoutUserDropdown>
        </SidebarMenuItem>
      </SidebarMenu>
    </SidebarFooter>
  )
}

import { createContext, useContext, useState, useCallback, ElementType, ReactNode, FC } from 'react'

export type Workspace = {
  name: string
  logo: ElementType
  plan: string
}

interface WorkspacesContextValue {
  workspaces: Workspace[]
  activeWorkspace: Workspace | null
  switchWorkspace: (workspace: Workspace) => void
}

interface WorkspacesProviderProps {
  children: ReactNode
  /** Initial list of workspaces to load */
  workspaces?: Workspace[]
}

const WorkspacesContext = createContext<WorkspacesContextValue | undefined>(undefined)

export const WorkspacesProvider: FC<WorkspacesProviderProps> = ({
  children,
  workspaces = [],
}) => {
  const [activeWorkspace, setActiveWorkspace] = useState<Workspace | null>(workspaces[0] ?? null)

  const switchWorkspace = useCallback((workspace: Workspace) => {
    setActiveWorkspace(workspace)
  }, [])

  return (
    <WorkspacesContext.Provider value={{ workspaces, activeWorkspace, switchWorkspace }}>
      {children}
    </WorkspacesContext.Provider>
  )
}

export const useWorkspaces = (): WorkspacesContextValue => {
  const context = useContext(WorkspacesContext)
  if (!context) {
    throw new Error('useWorkspaces must be inside a WorkspacesProvider')
  }
  return context
}

import { type AnyRoute, useRouter } from '@tanstack/react-router'
import { Home } from 'lucide-react'
import { ReactNode } from 'react'

export type SidebarItem = {
  key: string
  url: string
  icon: ReactNode
  title: ReactNode
  children: SidebarItem[]
}

const buildSidebarItems = (allFlatRoutes: AnyRoute[]): SidebarItem[] => {
  const items: SidebarItem[] = []

  const parents = allFlatRoutes.filter((route) => {
    if (!route.fullPath) return false

    // remove initial "/" and trailing "/" if present
    const cleanPath = route.fullPath.replace(/^\/+/, '').replace(/\/+$/, '')

    const segments = cleanPath.split('/')
    return segments.length === 1 && segments[0] !== ''
  })

  // root route
  const rootRoute = allFlatRoutes.find(
    (route) => route.fullPath === '/' && route.options?.staticData?.showInSidebar
  )
  if (rootRoute && rootRoute.options?.staticData) {
    items.push({
      key: 'root',
      url: '/',
      icon: rootRoute.options.staticData.icon || Home,
      title: rootRoute.options.staticData.title || 'Home',
      children: [],
    })
  }

  parents.forEach((parentRoute) => {
    if (!parentRoute.options?.staticData?.showInSidebar) return

    const parentPath = parentRoute.fullPath
    const parentStaticData = parentRoute.options.staticData

    const parentItem: SidebarItem = {
      key: parentPath,
      url: parentPath,
      icon: parentStaticData.icon || Home,
      title: parentStaticData.title || 'untitled',
      children: [],
    }

    const childRoutes = allFlatRoutes.filter((route) => {
      if (!route.fullPath || !route.options?.staticData?.showInSidebar) return false
      if (route.fullPath === parentPath) return false // skip the parent itself
      if (route.fullPath.includes('$')) return false // skip dynamic routes

      // check if this route is a child of the current parent
      const cleanPath = route.fullPath.replace(/^\/+/, '').replace(/\/+$/, '')
      const segments = cleanPath.split('/')

      // child routes should have more than 1 segment and start with the parent segment
      return (
        segments.length > 1 && segments[0] === parentPath.replace(/^\/+/, '').replace(/\/+$/, '')
      )
    })

    childRoutes.forEach((childRoute) => {
      if (childRoute.options?.staticData) {
        parentItem.children.push({
          key: childRoute.fullPath,
          url: childRoute.fullPath,
          icon: childRoute.options.staticData.icon || Home,
          title: childRoute.options.staticData.title || 'untitled',
          children: [],
        })
      }
    })

    items.push(parentItem)
  })

  return items
}

export const useDynamicSidebar = (): SidebarItem[] => {
  const router = useRouter()
  const allRoutes = router?.flatRoutes

  if (!allRoutes) {
    return []
  }

  return buildSidebarItems(allRoutes)
}
