import * as React from 'react'
import { cn } from '@/lib/utils'
import { LoadingSpinner } from '@/components/layout/LoadingSpinner'

export interface SpinProps {
  children: React.ReactNode
  spinning?: boolean
  className?: string
  overlayClassName?: string
  spinnerClassName?: string
  spinnerSize?: number
  blur?: boolean
}

export const Spin = React.forwardRef<HTMLDivElement, SpinProps>(
  (
    {
      children,
      spinning = false,
      className,
      overlayClassName,
      spinnerClassName,
      spinnerSize = 24,
      blur = true,
      ...props
    },
    ref
  ) => {
    return (
      <div ref={ref} className={cn('relative', className)} {...props}>
        {children}
        {spinning && (
          <div
            className={cn(
              'absolute inset-0 flex items-center justify-center bg-background/50',
              blur && 'backdrop-blur-xs',
              overlayClassName
            )}
          >
            <LoadingSpinner size={spinnerSize} className={cn('text-primary', spinnerClassName)} />
          </div>
        )}
      </div>
    )
  }
)

Spin.displayName = 'Spin'

import { cn } from '@/lib/utils'

export interface ISVGProps extends React.SVGProps<SVGSVGElement> {
  size?: number
  className?: string
}

export const LoadingSpinner = ({
  size = 24,
  className,
  ...props
}: ISVGProps) => {
  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      width={size}
      height={size}
      {...props}
      viewBox="0 0 24 24"
      fill="none"
      stroke="currentColor"
      strokeWidth="2"
      strokeLinecap="round"
      strokeLinejoin="round"
      className={cn('animate-spin', className)}>
      <path d="M21 12a9 9 0 1 1-6.219-8.56" />
    </svg>
  )
}
